mapper.select:
SELECT *
FROM
    (
    SELECT
        ROWNUM as rn,
        tbl.*
    FROM(
        SELECT * FROM mypage
        WHERE id = #{userId}
        ORDER BY reg_date DESC
        ) tbl
    )

WHERE rn > #{pageStart} AND rn <= #{pageEnd}

		SELECT COUNT(*)
		FROM mypage
        WHERE id = #{userId}
